package com.example.gymapp;

import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;
import androidx.recyclerview.widget.StaggeredGridLayoutManager;

import android.os.Bundle;

import java.util.ArrayList;

public class MusicOption extends AppCompatActivity {
    RecyclerView recyclerView;
    DeviceAdapter deviceAdapter;
    StaggeredGridLayoutManager layoutManager;
    ArrayList<Device> deviceArrayList;



    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_music_option);



        deviceArrayList = new ArrayList<>();
        deviceArrayList.add(new Device("Mix Music", "https://yt3.ggpht.com/ytc/AKedOLTvTEcsPAvkcqL0wnoMeMAioijLE8vee0RyXMB8YA=s900-c-k-c0x00ffffff-no-rj", "O7x2YwnVrs4"));
        deviceArrayList.add(new Device("Morning Music", "https://i.scdn.co/image/ab67616d0000b273384cae088bf1bcc4376d69c0", "XXkXvTR7IL8"));
        deviceArrayList.add(new Device("Chill Music", "https://i.ytimg.com/vi/SwpkPf63304/maxresdefault.jpg", "3UmDXL4gg2s"));
        deviceArrayList.add(new Device("Rock Music", "data:image/png;base64,", "ILO4T1kGlMM"));
        deviceArrayList.add(new Device("Hardcore Music", "https://thumbs.dreamstime.com/b/hardcore-music-style-143308554.jpg", "gEizosFZ-hs"));
        deviceArrayList.add(new Device("Motivated Music", "data:image/jpeg;base64,", "iqqEnK6JVmE"));


        recyclerView = findViewById(R.id.RecyclleView);
        deviceAdapter = new DeviceAdapter(deviceArrayList, this);
        layoutManager = new StaggeredGridLayoutManager(2, LinearLayoutManager.VERTICAL);
        recyclerView.setAdapter(deviceAdapter);
        recyclerView.setLayoutManager(layoutManager);

    }
}